const collapsible=document.querySelector(".collapsible"),links=document.querySelectorAll(".nav-link"),sections=document.querySelectorAll("section");function collapse(){collapsible.classList.remove("show")}function toggleCollapse(){collapsible.classList.contains("show")?collapsible.classList.remove("show"):collapsible.classList.add("show")}function isInViewport(l){const e=window.innerHeight,o=l.getBoundingClientRect();return o.top<e-50&&o.bottom>50}function addHighlight(){let l=!1;sections.forEach(e=>{let o="a[href='#"+e.id+"']",i=document.querySelector(o);i.classList.remove("highlighted"),isInViewport(e)&&!l&&(i.classList.add("highlighted"),l=!0)})}window.onload=(()=>{links.forEach(l=>l.addEventListener("click",collapse,!1))}),window.onscroll=(()=>{addHighlight()});